mapper.update:
update myhub.question

         title = #{title},
         `description` = #{description},
        tag = #{tag},
        gmt_modify= #{gmtModify}

    where id = #{id};

mapper.select:
select * from myhub.question


        title like  concat('%',#{key},'%')


order by gmt_modify desc limit  #{offset},#{size}
